.app {
  font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
  color: #0a2540;
}

.hero-section {
  min-height: 100vh;
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  text-align: center;
  color: white;
}

.hero-section h1 {
  font-size: 4rem;
  font-weight: 700;
  max-width: 800px;
  margin-bottom: 1.5rem;
}

.hero-section p {
  font-size: 1.25rem;
  opacity: 0.9;
  max-width: 600px;
}

.features-section {
  padding: 6rem 2rem;
  background: #f6f9fc;
}

.features-section h2 {
  font-size: 2.5rem;
  text-align: center;
  margin-bottom: 3rem;
}

.features-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
}

.feature-card {
  background: white;
  padding: 2rem;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0,0,0,0.05);
}

.products-section {
  padding: 6rem 2rem;
}

.developers-section {
  padding: 6rem 2rem;
  background: #0a2540;
  color: white;
}

.developers-section pre {
  background: #1a365d;
  padding: 2rem;
  border-radius: 8px;
  overflow-x: auto;
}

.enterprise-section {
  padding: 6rem 2rem;
  background: #f6f9fc;
}

.footer {
  padding: 4rem 2rem;
  background: #0a2540;
  color: white;
}

