export function FeaturesSection() {
  const features = [
    {
      title: 'Payments',
      description: 'Accept payments online, in person, or through your platform.'
    },
    {
      title: 'Billing',
      description: 'Build and scale your recurring business model.'
    },
    {
      title: 'Connect',
      description: 'Set up payments for platforms or marketplaces.'
    },
    {
      title: 'Payouts',
      description: 'Send funds to sellers and service providers worldwide.'
    }
  ]

  return (
    <section className="features-section">
      <h2>A fully integrated suite of payments products</h2>
      <div className="features-grid">
        {features.map((feature, index) => (
          <div key={index} className="feature-card">
            <h3>{feature.title}</h3>
            <p>{feature.description}</p>
          </div>
        ))}
      </div>
    </section>
  )
}

